import React, { Component }  from 'react';
import ListItem from './ListItem.js';

class SearchResults extends Component
{
  constructor() {
    super();
    this.textChanged = this.textChanged.bind(this)
    this.searchRequest = this.searchRequest.bind(this)
    this.state = {
      query: "",
      movieData: [],
      initialSearch: true
    };
  }

  textChanged(e) {
    this.setState({
      query: e.target.value
    });
  }

  searchRequest(e) {
    console.log(this.state.query);
    if (this.state.initialSearch) {
      this.setState({
        initialSearch: false
      });
    }
    //Here make a request to the server
    const url = 'http://192.168.1.168:3000/api/search/movie/' + this.state.query;
    const proxyurl = 'https://cors-anywhere.herokuapp.com/'
    fetch(url)
    .then(response => response.json())
    .then(data => {
      console.log(data)
      this.setState({movieData: data});
      this.forceUpdate()
    })
  }

  render()
  {
    let movies = this.state.movieData.map(function(movie){
      //For now just give links to the four streaming services that we have
      return <ListItem poster={movie.poster_small} title={movie.title} sources={movie} />;
    });

    if (this.state.initialSearch) {
      return (
        <div style={{
          position: 'absolute', left: '25%', top: '50%',
          width: '50%'
        }}>
          <div>Where Can I Watch?</div>
          <div style={{display: 'flex'}}>
            <input type="text" placeholder="Search..." onChange={this.textChanged} style={{
              width: '100%',
              height: '5vh'
            }} />
            <input type="submit" value='Search' onClick={this.searchRequest} />
          </div>
        </div>
      );
    } else {
      return (
        <div style={{
          position: 'absolute', left: '25%', top: '5%',
          width: '50%'
        }}>
          <div>Where Can I Watch?</div>
          <div style={{display: 'flex'}}>
            <input type="text" placeholder="Search..." onChange={this.textChanged} style={{
              width: '100%',
              height: '5vh'
            }} />
            <input type="submit" value='Search' onClick={this.searchRequest} />
          </div>
          <div style={{height: '500px', overflowY: 'scroll'}}>
            {movies}
          </div>
        </div>
      );
    }
  }
}

export default SearchResults;
