import React, { Component }  from 'react';
import netflix from './icons/netflix.png';
import amazon from './icons/amazon.png';
import hulu from './icons/hulu.png';
import hbo from './icons/hbo.png';

class ListItem extends Component
{
  constructor(props) {
    super(props);
    this.state = {
      poster: props.poster,
      title: props.title,
      sources: props.sources,
      netflix: "",
      hulu: "",
      hbo: "",
      amazon: ""
    };

    props.sources.free_web_sources.forEach(src => {
      if (src.source.includes('netflix'))
        this.setState({netflix: src.link})
      if (src.source.includes('hulu'))
        this.setState({hulu: src.link})
      if (src.source.includes('hbo'))
        this.setState({hbo: src.link})
      if (src.source.includes('amazon'))
        this.setState({amazon: src.link})
    })
  }

  render()
  {
    return (
      <div style={{
        display: 'flex',
        border: '1px solid black',
        margin: '10px 10px 10px 0px',
        padding: '5px'
      }}>
        <img src={this.state.poster} width='120vh' height='171vh' />
        <div>
          <div>{this.state.title}</div>
          <div style={{
            display: 'flex',
            justifyContent: 'space-between'
          }}>
            <a href='https://www.netflix.com'>
              <img src={netflix} width='32px' height='32px' style={{padding: '10px 10px 0px 10px', visibility: 'visible'}} />
            </a>
            <a href='https://www.hulu.com'>
              <img src={hulu} width='32px' height='32px' style={{padding: '10px 10px 0px 0px', visibility: 'visible'}} />
            </a>
            <a href='https://www.amazon.com'>
              <img src={amazon} width='32px' height='32px' style={{padding: '10px 10px 0px 0px', visibility: 'visible'}} />
            </a>
            <a href='https://www.hbo.com'>
              <img src={hbo} width='32px' height='32px' style={{padding: '10px 10px 0px 0px', visibility: 'visible'}} />
            </a>
          </div>
        </div>
      </div>
    );
  }
}

export default ListItem;
