        this.setState({hulu: src.link, hulu_vis: 'initial'})
      if (src.source.includes('hbo'))
        this.setState({hbo: src.link, hbo_vis: 'initial'})
      if (src.source.includes('amazon'))
        this.setState({amazon: src.link, amazon_vis: 'initial'})
    })
    this.state.sources.subscription_web_sources.forEach(src => {
      if (src.source.includes('netflix'))
        this.setState({netflix: src.link, netflix_vis: 'initial'})
      if (src.source.includes('hulu'))
        this.setState({hulu: src.link, hulu_vis: 'initial'})
      if (src.source.includes('hbo'))
        this.setState({hbo: src.link, hbo_vis: 'initial'})
      if (src.source.includes('amazon'))
        this.setState({amazon: src.link, amazon_vis: 'initial'})
    })
    this.state.sources.purchase_web_sources.forEach(src => {
      if (src.source.includes('netflix'))
        this.setState({netflix: src.link, netflix_vis: 'initial'})
      if (src.source.includes('hulu'))
        this.setState({hulu: src.link, hulu_vis: 'initial'})
      if (src.source.includes('hbo'))
        this.setState({hbo: src.link, hbo_vis: 'initial'})
      if (src.source.includes('amazon'))
        this.setState({amazon: src.link, amazon_vis: 'initial'})
    })*/
  }

  render()
  {
    return (
      <div style={{
        display: 'flex',
        border: '1px solid black',
        margin: '10px 10px 10px 0px',
        padding: '5px'
      }}>
        <img src={this.state.poster} width='120vh' height='171vh' />
        <div>
          <div>{this.state.title}</div>
          <div style={{
            display: 'flex',
            justifyContent: 'space-between'
          }}>
            <a href={this.state.netflix}>
              <img src={netflix} width='32px' height='32px' style={{padding: '10px 10px 0px 10px', display: this.state.netflix_vis}} />
            </a>
            <a href={this.state.hulu}>
              <img src={hulu} width='32px' height='32px' style={{padding: '10px 10px 0px 0px', display: this.state.hulu_vis}} />
            </a>
            <a href={this.state.amazon}>
              <img src={amazon} width='32px' height='32px' style={{padding: '10px 10px 0px 0px', display: this.state.amazon_vis}} />
            </a>
            <a href={this.state.hbo}>
              <img src={hbo} width='32px' height='32px' style={{padding: '10px 10px 0px 0px', display: this.state.hbo_vis}} />
            </a>
          </div>
        </div>
      </div>
    );
  }
}

export default ListItem;
