import React, { Component }  from 'react';
import netflix from './icons/netflix.png';
import amazon from './icons/amazon.png';
import hulu from './icons/hulu.png';
import hbo from './icons/hbo.png';
import disney from './icons/disneyplus.jpg';
import fandango from './icons/fandango.png';
import google from './icons/googleplay.jpg';
import itunes from './icons/itunes.jpg';
import verizon from './icons/verizon.jpg';
import vudu from './icons/vudu.png';
import youtube from './icons/youtube.png';
import tubi from './icons/tubi.png';

class ListItem extends Component
{
  constructor(props) {
    super(props);
    this.logos = {
      netflix: netflix,
      hulu: hulu,
      amazon_prime: amazon,
      amazon_buy: amazon,
      vudu: vudu,
      vudu_free: vudu,
      disney_plus: disney,
      hbo: hbo,
      google_play: google,
      youtube_purchase: youtube,
      verizon_on_demand: verizon,
      mgo: fandango,
      itunes: itunes,
      tubi_tv: tubi
    }
    this.state = {
      poster: props.poster,
      title: props.title,
      sources: props.sources,
      netflix: "",
      netflix_vis: 'none',
      hulu: "",
      hulu_vis: 'none',
      hbo: "",
      hbo_vis: 'none',
      amazon: "",
      amazon_vis: 'none'
    };
  }

  componentWillMount() {
    this.icons = this.state.sources.subscription_web_sources.map(function(src){
      //For now just give links to the four streaming services that we have
      let icon = null;
      if (src.source.includes('netflix'))
        icon = netflix
      if (src.source.includes('hulu'))
        icon = hulu
      if (src.source.includes('hbo'))
        icon = hbo
      if (src.source.includes('amazon'))
        icon = amazon
      return <a href={src.link}>
        <img src={icon} width='32px' height='32px' style={{padding: '10px 10px 0px 10px'}} />
      </a>;
    });
