import React, { Component }  from 'react';
import ListItem from './ListItem.js';

class SearchResults extends Component
{
  constructor(props) {
    super(props);
    this.textChanged = this.textChanged.bind(this)
    this.searchRequest = this.searchRequest.bind(this)
    this.state = {
      query: "",
      movieData: props.movies,
      initialSearch: true
    };
  }

  textChanged(e) {
    this.setState({
      query: e.target.value
    });
  }

  searchRequest(e) {
    console.log(this.state.query);
    if (this.state.initialSearch) {
      this.setState({
        initialSearch: false
      });
    }
    //Here make a request to the server
  }

  render()
  {
    let movies = this.state.movieData.map(function(movie){
      //For now just give links to the four streaming services that we have
      return <ListItem poster={movie.poster} title={movie.title} availability={movie.availability} links={movie.links} />;
    });

    if (this.state.initialSearch) {
      return (
        <div style={{
          position: 'absolute', left: '25%', top: '50%',
          width: '50%'
        }}>
          <div>WCIW</div>
          <div style={{display: 'flex'}}>
            <input type="text" placeholder="Search..." onChange={this.textChanged} style={{
              width: '100%',
              height: '5vh'
            }} />
            <input type="submit" value='Search' onClick={this.searchRequest} />
          </div>
        </div>
      );
    } else {
      return (
        <div style={{
          position: 'absolute', left: '25%', top: '5%',
          width: '50%'
        }}>
          <div>WCIW</div>
          <div style={{display: 'flex'}}>
            <input type="text" placeholder="Search..." onChange={this.textChanged} style={{
              width: '100%',
              height: '5vh'
            }} />
            <input type="submit" value='Search' onClick={this.searchRequest} />
          </div>
          <ul>{movies}</ul>
          <ListItem poster="https://images-na.ssl-images-amazon.com/images/I/61kspyBEsNL._SY679_.jpg" title="hobbis" />
          <ListItem poster="https://images-na.ssl-images-amazon.com/images/I/61kspyBEsNL._SY679_.jpg" title="slobbis" />
          <ListItem poster="https://images-na.ssl-images-amazon.com/images/I/61kspyBEsNL._SY679_.jpg" />
        </div>
      );
    }
  }
}

export default SearchResults;
