import React, { Component }  from 'react';
import netflix from './icons/netflix.png';
import amazon from './icons/amazon.png';
import hulu from './icons/hulu.png';
import hbo from './icons/hbo.png';

class ListItem extends Component
{
  constructor(props) {
    super(props);
    this.state = {
      poster: props.poster,
      title: props.title,
      sources: props.sources,
      netflix: "",
      netflix_vis: 'none',
      hulu: "",
      hulu_vis: 'none',
      hbo: "",
      hbo_vis: 'none',
      amazon: "",
      amazon_vis: 'none'
    };
  }

  componentWillMount() {
    this.icons = this.state.sources.subscription_web_sources.map(function(src){
      //For now just give links to the four streaming services that we have
      let icon = null;
      if (src.source.includes('netflix'))
        icon = netflix
      if (src.source.includes('hulu'))
        icon = hulu
      if (src.source.includes('hbo'))
        icon = hbo
      if (src.source.includes('amazon'))
        icon = amazon
      return <a href={src.link}>
        <img src={icon} width='32px' height='32px' style={{padding: '10px 10px 0px 10px'}} />
      </a>;
    });
